# create a word and sentence list
sentences = []
words = []

raw_text.each do |quote|
  tmp_words = quote.split(' ')
  tmp_sentences = quote.split(/\./)
  sentences += tmp_sentences
  words += tmp_words
end

# delete sentences consisting of only interpunction
sentences.reject! do |sentence|
  sentence.length < 5
end

# remove whitespace from sentences
sentences.each do |sentence|
  sentence.strip!  
end

# remove uninteresting and confusing words
words.reject! do |word|
 %w(and a an - the it).include? word
end

# format words by removing interpunction
words.each do |word| 
  word.gsub!(/[\.\'\,\?\!]/, '')
  word.downcase!
end

sentences.each do |sentence|
  Sentence.create(text: sentence)
end

words.each do |word|
  Word.create(text: word)
end

puts "Created #{Word.count} words!"
